var Glamour = require(__dirname + '/src/glamour.js');
var fs = require('fs');

module.exports = function (dependencies)
{
    return (function (undefined)
    {
        if (dependencies == undefined) {
            if (fs.existsSync(process.cwd() + '/glamour.json'))
                dependencies = require(process.cwd() + '/glamour.json');
            else
                dependencies = [];
        }
        return Glamour.run(process.cwd(), dependencies);
    })();
};